import React, { useState, useRef, useEffect } from 'react';
import './App.css';

const App = () => {
  const Ref = useRef(null);

  const [timer, setTimer] = useState('05:00');
  const [isRunning, setIsRunning] = useState(false);
  const [initialTime, SetinitialTime] = useState(300);
  const [isBlinking, setIsBlinking] = useState(false);

  // Function to load the initialTime from localStorage
  const loadInitialTime = () => {
    const savedTime = localStorage.getItem('initialTime');
    if (savedTime) {
      SetinitialTime(parseInt(savedTime, 10));
    }
  };

  // Function to save the initialTime to localStorage
  const saveInitialTime = (value) => {
    localStorage.setItem('initialTime', value.toString());
  };

  const getTimeRem = (e) => {
    const tot = Date.parse(e) - Date.parse(new Date());
    const sec = Math.floor((tot / 1000) % 60);
    const min = Math.floor((tot / 1000 / 60) % 60);
    return {
      tot,
      min,
      sec,
    };
  };

  const startTimer = (e) => {
    let { tot, min, sec } = getTimeRem(e);
    if (tot >= 0) {
      setTimer(
        (min > 9 ? min : '0' + min) + ':' + (sec > 9 ? sec : '0' + sec)
      );
      setIsBlinking(tot < 30000);
    }
  };

  const clearTIme = (e) => {
    setTimer('05:00');
    if (Ref.current) clearInterval(Ref.current);
    const id = setInterval(() => {
      startTimer(e);
    }, 1000);
    Ref.current = id;
  };

  const getDeadTime = () => {
    let deadline = new Date();
    deadline.setSeconds(deadline.getSeconds() + initialTime);
    return deadline;
  };

  useEffect(() => {
    // Load the initialTime when the component mounts
    loadInitialTime();
  }, []);

  useEffect(() => {
    if (isRunning) {
      saveInitialTime(initialTime);
      clearTIme(getDeadTime());
    }
  }, [isRunning, initialTime]);

  const onClickStart = () => {
    setIsRunning(true);
  };

  const onClickStop = () => {
    setIsRunning(false);
    if (Ref.current) clearInterval(Ref.current);
  };

  const onClickReset = () => {
    setIsRunning(false);
    SetinitialTime(32);
    setTimer('00:32');
    localStorage.removeItem('initialTime');
  };

  return (
    <div className="App">
      <div className="box">
        <h2 className={`center ${isBlinking ? 'blink' : ''} ${isBlinking ? 'red' : 'white'}`}>{timer}</h2>
        <div className="button-cont">
          <button onClick={onClickStart} className='btn'>Start</button>
          <button onClick={onClickStop} className='btn'>Stop</button>
          <button onClick={onClickReset} className='btn-reset'>Reset</button>
        </div>
      </div>
    </div>
  );
};

export default App;
