import React,{useState,useRef,useEffect} from 'react';

const App=()=>{
  const Ref=useRef(null);

  const [timer,setTimer]=useState('00:00');
  const getTimeRem=(e)=>{
    const tot=Date.parse(e)-Date.parse(new Date());
    const sec=Math.floor((tot/1000)%60);
    const min=Math.floor((tot/1000/60)%60);
    return{
      tot,min,sec
    };
  }
  const startTimer=(e)=>{
    let{tot,min,sec}=getTimeRem(e);
    if(tot>=0){
      setTimer(
        (min>9?min:'0'+min)+':'+(sec>9 ? sec :'0'+sec)
      )
    }
  }
  const clearTIme=(e)=>{
    setTimer('05:00');
    if(Ref.current) clearInterval(Ref.current);
    const id=setInterval(()=>{
      startTimer(e);
    },1000)
    Ref.current=id;
  }

  const getDeadTime=()=>{
    let deadine=new Date();
    deadine.setSeconds(deadine.getSeconds()+10);
    return deadine;
  }
  useEffect(()=>{
    clearTIme(getDeadTime());
  },[]);
  const onClickReset=()=>{
    clearTIme(getDeadTime());
  }

  return(
    <div className="App">
      <h2>{timer}</h2>
      <button onClick={onClickReset}>Start</button>

    </div>
  )
}
export default App;
